hello:
void hello() {
    // "Hello, world!" を出力する
    cout << "Hello, world!" << endl;
}

add:
double add(double a, double b) {
    // 2つの浮動小数点数を加算して返す
    return a + b;
}

main:
int main() {
    // "Hello, world!" を出力する関数を呼び出す
    hello();
    // 3と5を加算して結果を出力する
    cout << "Add: " << add(3, 5) << endl;
    // 4と6を乗算して結果を出力する
    cout << "Multiply: " << Math().multiply(4, 6) << endl;
    // 5の二乗を計算して結果を出力する
    cout << "Square: " << Math::square(5) << endl;
    // 10と20のうち大きい方を出力する
    cout << "Max: " << max_value(10, 20) << endl;
    return 0;
}
